&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b)
{
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; a&amp;lt;b?a:b;
}
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b,&lt;span class="hljs-type"&gt;int&lt;/span&gt; c)
{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; d&amp;#61;a&amp;lt;b?a:b;
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; c&amp;lt;d?c:d;
}
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b,&lt;span class="hljs-type"&gt;int&lt;/span&gt; c,&lt;span class="hljs-type"&gt;int&lt;/span&gt; d)
{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; e&amp;#61;a&amp;lt;b?a:b;
&lt;span class="hljs-type"&gt;int&lt;/span&gt; f&amp;#61;c&amp;lt;d?c:d;
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; e&amp;lt;f?e:f;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;上面用的三元运算符。也可以用判断做&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b)
{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min&amp;#61;a;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(b&amp;lt;min)min&amp;#61;b;
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; min;
}
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b,&lt;span class="hljs-type"&gt;int&lt;/span&gt; c)
{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min&amp;#61;a;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(b&amp;lt;min)min&amp;#61;b;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(c&amp;lt;min)min&amp;#61;c;
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; min;
}
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b,&lt;span class="hljs-type"&gt;int&lt;/span&gt; c,&lt;span class="hljs-type"&gt;int&lt;/span&gt; d)
{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min&amp;#61;a;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(b&amp;lt;min)min&amp;#61;b;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(c&amp;lt;min)min&amp;#61;c;
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(d&amp;lt;min)min&amp;#61;d;
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; min;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;以上只针对作业。其实最正规的做法应该是这样&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;min&lt;/span&gt;&lt;span class="hljs-params"&gt;(params &lt;span class="hljs-type"&gt;int&lt;/span&gt;[] a)&lt;/span&gt;
&lt;/span&gt;{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; min&amp;#61;a[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-built_in"&gt;sizeof&lt;/span&gt;(a);i&amp;#43;&amp;#43;)
{
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(a[i]&amp;lt;min)min&amp;#61;a[i];
}
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; min;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;根本用不着什么鬼重载&lt;/p&gt;

void main()
{
	int M,N;

	int nub;

	int tmp1,tmp2 = 0;

	//键盘输入M,N的值
	printf("请输入M,N的值：\n");
	scanf("%d%d" , &amp;M,&amp;N);

	for(tmp1 = M;tmp1 &lt; N;tmp1++)
	{
		if((tmp1 % 3 == 0) &amp;&amp; (tmp1 % 5 == 0))
		{
			tmp2++;
			printf("%d\n" , tmp1);
		}
	}

	printf("%d到%d之间能3和5同时整除的整数个数为：",M,N);
	printf("%d\n",tmp2);
}

