void splitfloat(float x, int *intpart, float *fracpart)
{
	std::string srcData =std:: to_string(x);
	std::string temp;
	int position=-1;
	for (int i = 0; i &lt; srcData.length(); i++)
	{
		if (srcData[i]=='.')
		{
			position = i;
			break;
		}
	}
	for (int i = 0; i &lt; position; i++)
	{
		temp = temp + srcData[i];
	}
	int tempValue;
	tempValue = stoi(temp);
	*intpart=tempValue;
	temp = "0";
	for (int i = position; i &lt; srcData.length(); i++)
	{
		temp = temp + srcData[i];
	}
	float tempData;
	tempData = stof(temp);
	*fracpart = tempData;

}
