    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; sum&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; &lt;span class="hljs-comment"&gt;//存储奇数的和 &lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; num;  &lt;span class="hljs-comment"&gt;//存储输入的n个数的临时变量 &lt;/span&gt;

&lt;span class="hljs-comment"&gt;//    printf(&amp;#34;请输入一个正整数&amp;#xff1a;&amp;#34;);&lt;/span&gt;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(i&amp;lt;n){

        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;num);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(num&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;&amp;amp;&amp;amp;num%&lt;span class="hljs-number"&gt;2&lt;/span&gt;!&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;){  &lt;span class="hljs-comment"&gt;//如果是奇数,则把它累加到sum中 &lt;/span&gt;
            sum&amp;#43;&amp;#61;num;
        }
        i&amp;#43;&amp;#43;;
    }

    &lt;span class="hljs-comment"&gt;//打印结果 &lt;/span&gt;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,sum);

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
} 
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/150552009266188.png" class="md_img" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;

&lt;p&gt;有如下问题&amp;#xff1a;&lt;br /&gt;1.最后printf中的&amp;amp;符号要去掉,printf中打印变量值是不需要这个的&lt;br /&gt;2.需要判断如何跳出循环输出结果&amp;#xff0c;因为是换行出结果&amp;#xff0c;所以我们判断输入的字符是否有换行&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;string.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; sum &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;a) !&amp;#61; EOF){
        sum &amp;#43;&amp;#61; a;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;getchar&lt;/span&gt;() &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;\n&amp;#39;&lt;/span&gt;){
            &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
        }
    }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d\n&amp;#34;&lt;/span&gt;,sum);
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;对您有帮助的话希望可以采纳&lt;/p&gt;

&lt;pre&gt;&lt;code class="language-c"&gt;
&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; main()
{
    &lt;span class="hljs-keyword"&gt;int&lt;/span&gt; a, b &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d %d&amp;#34;&lt;/span&gt;, &amp;amp;a, &amp;amp;b);
    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Sum:%d\n&amp;#34;&lt;/span&gt;, a &amp;#43; b);
    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Difference:%d&amp;#34;&lt;/span&gt;, a - b);

}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-python"&gt;total &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &lt;span class="hljs-comment"&gt;#整数总和&lt;/span&gt;
count &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &lt;span class="hljs-comment"&gt;#数量总和&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;while&lt;/span&gt; &lt;span class="hljs-literal"&gt;True&lt;/span&gt;: &lt;span class="hljs-comment"&gt;#无限循环&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;integer&lt;/span&gt; &amp;#61; input() &lt;span class="hljs-comment"&gt;#请求输入&lt;/span&gt;

    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-keyword"&gt;integer&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#34;#&amp;#34;&lt;/span&gt;:
        &lt;span class="hljs-keyword"&gt;break&lt;/span&gt; &lt;span class="hljs-comment"&gt;#跳出循环&lt;/span&gt;

    total &amp;#43;&amp;#61; &lt;span class="hljs-keyword"&gt;int&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;integer&lt;/span&gt;)
    count &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;print&lt;/span&gt;(count, total)
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;import random&lt;/p&gt;

