    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d - %d &amp;#61; %d\n&amp;#34;&lt;/span&gt;,a,b,a-b);
    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d * %d &amp;#61; %d\n&amp;#34;&lt;/span&gt;,a,b,a*b);
    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d / %d &amp;#61; %d\n&amp;#34;&lt;/span&gt;,a,b,a/b);
