    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; MAXSIZE 1000&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title class_"&gt;MySort&lt;/span&gt;
{
      &lt;span class="hljs-keyword"&gt;private&lt;/span&gt;:
          &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[MAXSIZE];
          &lt;span class="hljs-type"&gt;int&lt;/span&gt; len;
      &lt;span class="hljs-keyword"&gt;public&lt;/span&gt;:
          &lt;span class="hljs-built_in"&gt;MySort&lt;/span&gt;() {len &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;}
          &lt;span class="hljs-built_in"&gt;MySort&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *p ,&lt;span class="hljs-type"&gt;int&lt;/span&gt; n) {len &amp;#61; n; &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;) a[i] &amp;#61; p[i];}
          &lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;Sort&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;bool&lt;/span&gt; asc)&lt;/span&gt;
          &lt;/span&gt;{
                &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;i&amp;#43;&amp;#43;)
                  &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;len -i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;j&amp;#43;&amp;#43;)
                  {
                      &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;((asc &amp;amp;&amp;amp; a[j] &amp;gt; a[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]) || (!asc &amp;amp;&amp;amp; a[j] &amp;lt; a[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;]))
                      {
                            &lt;span class="hljs-type"&gt;int&lt;/span&gt; t &amp;#61; a[j];
                            a[j] &amp;#61; a[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
                            a[j&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; t;
                      }
                  }
          }
          &lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;print&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt; &lt;/span&gt;{ &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;) &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d &amp;#34;&lt;/span&gt;,a[i]);&lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);}
          &lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;setArray&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *p,&lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt; &lt;/span&gt;{len &amp;#61; n; &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;len;i&amp;#43;&amp;#43;) a[i] &amp;#61; p[i];}
};

