    scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;&lt;span class="hljs-built_in"&gt;n&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;if&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;n&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
    {
        &lt;span class="hljs-built_in"&gt;count&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;max&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;min&lt;/span&gt; &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    }
    while (&lt;span class="hljs-built_in"&gt;n&lt;/span&gt;)
    {
        x &amp;#61; &lt;span class="hljs-built_in"&gt;n&lt;/span&gt; % &lt;span class="hljs-number"&gt;10&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;if&lt;/span&gt; (x &amp;gt; &lt;span class="hljs-built_in"&gt;max&lt;/span&gt;)
            &lt;span class="hljs-built_in"&gt;max&lt;/span&gt; &amp;#61; x;
        &lt;span class="hljs-built_in"&gt;if&lt;/span&gt; (x &amp;lt; &lt;span class="hljs-built_in"&gt;min&lt;/span&gt;)
            &lt;span class="hljs-built_in"&gt;min&lt;/span&gt; &amp;#61; x;
        &lt;span class="hljs-built_in"&gt;n&lt;/span&gt; /&amp;#61; &lt;span class="hljs-number"&gt;10&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;count&lt;/span&gt;&amp;#43;&amp;#43;;
    }
    printf(&lt;span class="hljs-string"&gt;&amp;#34;位数: %d\n最大数字: %d\n最小数字: %d\n&amp;#34;&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;count&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;max&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;min&lt;/span&gt;);
    return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-comment"&gt;#include &amp;lt;stdio.h&amp;gt;&lt;/span&gt;
