    printf(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);    
}


void mp&lt;span class="hljs-constructor"&gt;Sort(&lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;a&lt;/span&gt;[], &lt;span class="hljs-params"&gt;int&lt;/span&gt; &lt;span class="hljs-params"&gt;n&lt;/span&gt;)&lt;/span&gt;{
