        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; j &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; j &amp;lt; n - i - &lt;span class="hljs-number"&gt;1&lt;/span&gt;; j&amp;#43;&amp;#43;){
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt; &amp;gt; a&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt; &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;]&lt;/span&gt;){
