    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (r &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; a;
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-built_in"&gt;gcd_impl&lt;/span&gt;(r, a);
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-title"&gt;gcd&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; a, &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; b)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &amp;amp;&amp;amp; b &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; b;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (b &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; a;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;lt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
