    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%lld&amp;#34;&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;gcd&lt;/span&gt;(a, b));
