/*设head1和head2是两个非空单向链表，数据值有重复且升序排序
将head1和head2合并成一个升序链
*/
 
#include&lt;stdio.h&gt;
#include&lt;stdlib.h&gt;
 
//定义结构体单元 
typedef struct node{
	int data;//结构体数据域 
	struct node* next;//结构体指针域 
}ElemSN;
//逆向创建单向链表（头插法）
ElemSN *PreCreatLink(int a[],int n)
{
	ElemSN *head=NULL,*p;
	for(int i=n-1;i&gt;-1;i--)
	{
		p=(ElemSN*)malloc(sizeof(ElemSN));
		p-&gt;data=a[i];
		p-&gt;next=head;
		head=p;
	}
	return head;
 } 
 
 
 ElemSN* Combine(ElemSN *head1,ElemSN *head2)
 {
 	ElemSN *head,*p,*tail;
 	head=NULL;
 	while(head1&amp;&amp;head2)
 	{
 		if(head1-&gt;data&lt;head2-&gt;data)
 		{
 			p=head1;
 			head1=head1-&gt;next;
		 }
		else
		{
			p=head2;
			head2=head2-&gt;next;
		}
		p-&gt;next=NULL; 
		if(!head)
		head=tail=p;
		else
		{
			tail=tail-&gt;next=p;
		}
	 }
	 if(head1)
	 p=head1;
	 else
	 p=head2;
	 tail-&gt;next=p;
	 return head;
 }
 
   //打印链表所有结点的值
   void PrintLink(ElemSN* head)
 {
 	ElemSN *p=head;
 	for(;p;p=p-&gt;next)
 	printf("%5d",p-&gt;data);
 } 
  
 
 int main(void)
 {
 	ElemSN *head1,*head2,*head;
 	int a[7]={2,3,5,6,7,9,11};
 	int b[5]={2,4,5,7,9};
 	head1=PreCreatLink(a,7);    //创建链表head1 
 	head2=PreCreatLink(b,5);    //创建链表head2 
 	head=Combine(head1,head2);  //将两个有序链表合并成一个有序链表 
 	PrintLink(head);       //输出链表 
  } 

```


