                                                    case &lt;span class="hljs-string"&gt;&amp;#39;-&amp;#39;&lt;/span&gt;:
                                                    &lt;span class="hljs-attribute"&gt;left&lt;/span&gt;&amp;#61;left&amp;#43;sign*right;
                                                    &lt;span class="hljs-attribute"&gt;sign&lt;/span&gt;&amp;#61;-1;
                                                    &lt;span class="hljs-attribute"&gt;right&lt;/span&gt;&amp;#61;num[j&amp;#43;1];
                                                    break;
                                                    case &lt;span class="hljs-string"&gt;&amp;#39;*&amp;#39;&lt;/span&gt;:
                                                    &lt;span class="hljs-attribute"&gt;right&lt;/span&gt;&amp;#61;right*num[j&amp;#43;1];
                                                    break;
                                                    case &lt;span class="hljs-string"&gt;&amp;#39;/&amp;#39;&lt;/span&gt;:
                                                    &lt;span class="hljs-attribute"&gt;right&lt;/span&gt;&amp;#61;right/num[j&amp;#43;1];
                                                    break;
                                                }
                                             }
                    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(left&amp;#43;sign*&lt;span class="hljs-attribute"&gt;right&lt;/span&gt;&amp;#61;&amp;#61;result)
                    {
                        count&amp;#43;&amp;#43;;
                        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-attribute"&gt;j&lt;/span&gt;&amp;#61;1;j&amp;lt;&amp;#61;4;j&amp;#43;&amp;#43;)  
                            printf(&lt;span class="hljs-string"&gt;&amp;#34;%c &amp;#34;&lt;/span&gt;,oper[i[j]]);
                    }
                                    }
                                }
                        }
                }
            }
        }
    }
}
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-attribute"&gt;count&lt;/span&gt;&amp;#61;&amp;#61;0)
    printf(&lt;span class="hljs-string"&gt;&amp;#34;No solution&amp;#34;&lt;/span&gt;);
    getchar();
