问题解决的话，请点下采纳
```
#include &lt;stdlib.h&gt;
#include &lt;stdio.h&gt;
#include &lt;time.h&gt; 

int main()
{
	int n, x, y;
	while (1)
	{
		printf("请输入&lt;=20的正整数：");
		scanf("%d", &amp;n);
		if (n &gt; 0 &amp;&amp; n &lt;= 20) break;
	}
	printf("请输入x y：");
	scanf("%d%d", &amp;x, &amp;y);
	int arr[21];
	srand((unsigned)time(NULL));
	for (int i = 0; i &lt; n; i++)
	{
		arr[i] = rand() % (y - x) + x;
	}
	for (int i = 0; i &lt; n; i++)
		printf("%d\t", arr[i]);
	printf("\n");
	if (n &gt;= 3)
	{
		for (int i = 0; i &lt; 2; i++)
		{
			int t = arr[i];
			arr[i] = arr[n - i - 1];
			arr[n - i - 1] = t;
		}
	}
	for (int i = 0; i &lt; n; i++)
		printf("%d\t", arr[i]);
	printf("\n");
	return 0;
}
```

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; N 1000&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;swap&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *a, &lt;span class="hljs-type"&gt;int&lt;/span&gt; *b)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; t &amp;#61; *a;
    *a &amp;#61; *b;
    *b &amp;#61; t;
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;sort&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; A[], &lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n - &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i&amp;#43;&amp;#43;)
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; j &amp;#61; i &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; j &amp;lt; n; j&amp;#43;&amp;#43;)
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (A[i] &amp;gt; A[j])
                &lt;span class="hljs-built_in"&gt;swap&lt;/span&gt;(&amp;amp;A[i], &amp;amp;A[j]);
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i, j, x, n, A[N];
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;n);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, &amp;amp;A[i]);
    &lt;span class="hljs-built_in"&gt;sort&lt;/span&gt;(A, n);
    i &amp;#61; j &amp;#61; n / &lt;span class="hljs-number"&gt;2&lt;/span&gt;;
    x &amp;#61; A[i];
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (A[i] &amp;#61;&amp;#61; x)
        i--;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (A[j] &amp;#61;&amp;#61; x)
        j&amp;#43;&amp;#43;;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (i &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt; &amp;#61;&amp;#61; n - j)
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;, x);
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;-1&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;一个实现&amp;#xff0c;如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;



&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;void&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;{

    &lt;span class="hljs-type"&gt;int&lt;/span&gt; num;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; x,y;
