def f(n):
  sum = 0
  for i in range(n):
    if n % (i + 1) == 0:
      sum = sum + (i + 1) * (i + 1)
  return sum

def F(n):
  sum = 0
  for i in range(n):
    sum = sum + f(i + 1)
  return sum

