            {
                numCount[j]&amp;#43;&amp;#43;;
                break;
            }
        if (j&amp;#61;&amp;#61;k)
        {
            nums[k] &amp;#61; s;
            numCount[k] &amp;#61; 1;
            k&amp;#43;&amp;#43;;
        }
    }
    for (i &amp;#61; 1; i &amp;lt; k; i&amp;#43;&amp;#43;)
    {
        if (numCount[i] &amp;gt; numCount[index1])
            index1 &amp;#61; i;
        if (numCount[i] &amp;lt; numCount[index2])
            index2 &amp;#61; i;
    }
    printf(&amp;#34;最多的数: %d 出现 %d 次\n&amp;#34;, nums[index1],numCount[index1]);
