&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
&lt;span class="hljs-type"&gt;int&lt;/span&gt; n,min,num;
&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);
&lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;min);
&lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(n&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
{
  &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;num);
  &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(min&amp;gt;num)
    min&amp;#61;num;
  n--;
}
&lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,min);
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;供参考&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n, m, s &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;, &amp;amp;m, &amp;amp;n);
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (m &amp;lt;&amp;#61; n)
    {
        s &amp;#43;&amp;#61; m % &lt;span class="hljs-number"&gt;10&lt;/span&gt;;
        m&amp;#43;&amp;#43;;
    }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,s);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-comment"&gt;#include&amp;lt;stdio.h&amp;gt;&lt;/span&gt;
int main() {
    int n,i,m,t;
    scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-attribute"&gt;i&lt;/span&gt;&amp;#61;10;i&amp;lt;&amp;#61;n;i&amp;#43;&amp;#43;){
        &lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;0;
        &lt;span class="hljs-attribute"&gt;t&lt;/span&gt;&amp;#61;i;
        &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(t!&amp;#61;0){
            m&amp;#43;&amp;#61;t%10;
            &lt;span class="hljs-attribute"&gt;t&lt;/span&gt;&amp;#61;t/10;
        }
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i%&lt;span class="hljs-attribute"&gt;m&lt;/span&gt;&amp;#61;&amp;#61;0){
                printf(&lt;span class="hljs-string"&gt;&amp;#34;%d\n&amp;#34;&lt;/span&gt;,i);
        }    
    } 
    return 0;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;br /&gt; 觉得有用的话&lt;font color="red"&gt;&lt;strong&gt;采纳&lt;/strong&gt;&lt;/font&gt;一下哈&lt;/p&gt;

&lt;pre&gt;&lt;code class="language-xml"&gt;#include &amp;lt;stdio.h&amp;gt;
#define N 10

int main()
{
    int x&lt;span class="hljs-comment"&gt;[N]&lt;/span&gt;,y&lt;span class="hljs-comment"&gt;[N]&lt;/span&gt;;
    int m&lt;span class="hljs-comment"&gt;[N]&lt;/span&gt;,n&lt;span class="hljs-comment"&gt;[N]&lt;/span&gt;;
    int count&amp;#61;0,t;
    for(int i&amp;#61;0;i&amp;lt;N;i&amp;#43;&amp;#43;)
    {
        scanf(&lt;span class="hljs-string"&gt;&amp;#34;%d%d&amp;#34;&lt;/span&gt;,&amp;amp;m&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;,&amp;amp;n&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;);
        if(m&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;&amp;#61;0 &amp;amp;&amp;amp; n&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;&amp;#61;0)
            break;
        count&amp;#43;&amp;#43;;
    }

    for(int i&amp;#61;0;i&amp;lt;count;i&amp;#43;&amp;#43;)
    {
        for(x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;1;x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;lt;m&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;;x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#43;&amp;#43;)
        {
            y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;m&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;-x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;;
            if(n&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;%x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;&amp;#61;0 &amp;amp;&amp;amp; n&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;%y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;&amp;#61;0)
            {
                if(x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;gt;y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;)
                {
                    t&amp;#61;x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;;
                    x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;;
                    y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;&amp;#61;t;
                }
                break;
            }
        }
    }

    for(int i&amp;#61;0;i&amp;lt;count;i&amp;#43;&amp;#43;)
    {
        printf(&lt;span class="hljs-string"&gt;&amp;#34;%d %d\n&amp;#34;&lt;/span&gt;,x&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;,y&lt;span class="hljs-comment"&gt;[i]&lt;/span&gt;);
    }

    return 0;    
}
&lt;/code&gt;&lt;/pre&gt;

&lt;ul&gt;&lt;li&gt;你看下这篇博客吧, 应该有用&amp;#x1f449; &amp;#xff1a;&lt;a href="https://blog.csdn.net/qq_44364832/article/details/105595744?utm_source&amp;#61;csdn_ai_ada_ask_robot" target="_blank" rel="noopener noreferrer"&gt;&lt;font size="3"&gt;求n的阶乘时溢出的解决办法&lt;/font&gt;&lt;/a&gt;&lt;/li&gt;&lt;li&gt;除此之外, 这篇博客: &lt;a href="https://blog.csdn.net/weixin_62676902/article/details/121193533?utm_source&amp;#61;csdn_ai_ada_ask_robot" target="_blank" rel="noopener noreferrer"&gt;&lt;font size="3"&gt;输入一个正整数n(1 &amp;#xff1c; n &amp;#xff1c; 100)&amp;#xff0c;再输入n个整数&amp;#xff0c;将最小数与第一个数交换&amp;#xff08;若最小数就在第一位&amp;#xff0c;不需要交换&amp;#xff09;&amp;#xff0c;输出交换后的n个数。‪‬‪‬‪‬‪‬‪‬‮‬‭‬‪‬‪‬‪‬‪‬‪‬‪‬‮‬‪‬‪&lt;/font&gt;&lt;/a&gt;中的 &lt;strong&gt;解决方案&amp;#xff1a;&lt;/strong&gt; 部分也许能够解决你的问题, 你可以仔细阅读以下内容或跳转源博客中阅读: &lt;br /&gt;&lt;/li&gt;&lt;li&gt;&lt;p&gt;先设定变量&amp;#xff0c;由题可得正整数n(1 &amp;lt; n &amp;lt; 100)故设定变量num[100]。&lt;br /&gt;
根据题中描述共输入n个数字故利用设定变量i并使i&amp;lt;n后利用for循环完成输入。即&lt;br /&gt;
for(i&amp;#61;0;i&amp;lt;n;i&amp;#43;&amp;#43;)&lt;br /&gt;
{&lt;!-- --&gt;&lt;br /&gt;
scanf(&amp;#34;%d&amp;#34;,&amp;amp;num[i]);&lt;br /&gt;
}&lt;br /&gt;
将输入的第一个数字赋值给min&amp;#xff0c;即&amp;#xff0c;&lt;br /&gt;
min &amp;#61; num[0];&lt;br /&gt;
利用for循环比较出最小的数并得到该数的位置即&amp;#xff0c;&lt;br /&gt;
for(i&amp;#61;0;i&amp;lt;n;i&amp;#43;&amp;#43;)&lt;br /&gt;
{&lt;!-- --&gt;&lt;br /&gt;
if(min&amp;gt;num[i])&lt;br /&gt;
{&lt;!-- --&gt;&lt;br /&gt;
min &amp;#61; num[i];&lt;br /&gt;
minsign &amp;#61; i;&lt;br /&gt;
}&lt;br /&gt;
}&lt;br /&gt;
利用赋值变量的方法改变最小的数字和第一个数字的位置即&amp;#xff0c;&lt;br /&gt;
temp &amp;#61; num[0];&lt;br /&gt;
num[0] &amp;#61; min;&lt;br /&gt;
num[minsign] &amp;#61; temp;&lt;br /&gt;
最后利用循环输出换后的数组&amp;#xff0c;并结束即&lt;br /&gt;
for(i&amp;#61;0;i&amp;lt;n;i&amp;#43;&amp;#43;)&lt;br /&gt;
{&lt;!-- --&gt;&lt;br /&gt;
printf(&amp;#34;%d “,num[i]);&lt;br /&gt;
}&lt;br /&gt;
printf(”\n&amp;#34;);&lt;br /&gt;
return 0;&lt;br /&gt;
}&lt;/p&gt;
