 &lt;span class="token keyword"&gt;if&lt;/span&gt;&lt;span class="token punctuation"&gt;(&lt;/span&gt;min&lt;span class="token operator"&gt;&amp;gt;&lt;/span&gt;num&lt;span class="token punctuation"&gt;[&lt;/span&gt;i&lt;span class="token punctuation"&gt;]&lt;/span&gt;&lt;span class="token punctuation"&gt;)&lt;/span&gt;
 &lt;span class="token punctuation"&gt;{&lt;!-- --&gt;&lt;/span&gt;
  min &lt;span class="token operator"&gt;&amp;#61;&lt;/span&gt; num&lt;span class="token punctuation"&gt;[&lt;/span&gt;i&lt;span class="token punctuation"&gt;]&lt;/span&gt;&lt;span class="token punctuation"&gt;;&lt;/span&gt;
  minsign &lt;span class="token operator"&gt;&amp;#61;&lt;/span&gt; i&lt;span class="token punctuation"&gt;;&lt;/span&gt;
 &lt;span class="token punctuation"&gt;}&lt;/span&gt;
&lt;span class="token punctuation"&gt;}&lt;/span&gt;
temp &lt;span class="token operator"&gt;&amp;#61;&lt;/span&gt; num&lt;span class="token punctuation"&gt;[&lt;/span&gt;&lt;span class="token number"&gt;0&lt;/span&gt;&lt;span class="token punctuation"&gt;]&lt;/span&gt;&lt;span class="token punctuation"&gt;;&lt;/span&gt;
