    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-built_in"&gt;N&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-built_in"&gt;if&lt;/span&gt; (a[i] &amp;lt; &lt;span class="hljs-built_in"&gt;min&lt;/span&gt;)
