    for (i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-built_in"&gt;N&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i&amp;#43;&amp;#43;)
    {
