            &lt;span class="hljs-built_in"&gt;max&lt;/span&gt; &amp;#61; a[i];   //记录最大值
            &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; temp &amp;#61; a[&lt;span class="hljs-built_in"&gt;N&lt;/span&gt;-&lt;span class="hljs-number"&gt;1&lt;/span&gt;];
            a[&lt;span class="hljs-built_in"&gt;N&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; a[i];
            a[i] &amp;#61; temp;
            /* a[i] &amp;#61; &lt;span class="hljs-built_in"&gt;max&lt;/span&gt;;
             a[&lt;span class="hljs-built_in"&gt;N&lt;/span&gt; - &lt;span class="hljs-number"&gt;1&lt;/span&gt;] &amp;#61; i;
