&lt;pre&gt;&lt;code class="language-java"&gt;

&lt;span class="hljs-keyword"&gt;public&lt;/span&gt; &lt;span class="hljs-class"&gt;&lt;span class="hljs-keyword"&gt;class&lt;/span&gt; &lt;span class="hljs-title"&gt;A&lt;/span&gt; &lt;/span&gt;{
