        break;
    &lt;span class="hljs-built_in"&gt;if&lt;/span&gt; &lt;span class="hljs-built_in"&gt;n&lt;/span&gt; % &lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-symbol"&gt;0:&lt;/span&gt;
        &lt;span class="hljs-built_in"&gt;sum&lt;/span&gt; &amp;#43;&amp;#61; &lt;span class="hljs-built_in"&gt;n&lt;/span&gt;;
print(&lt;span class="hljs-string"&gt;&amp;#34;上述所有非负数中偶数之和为&amp;#xff1a;&amp;#34;&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;sum&lt;/span&gt;)
