&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt; &lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n, i, b &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, c &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    cin &amp;gt;&amp;gt; n;
    cout &amp;lt;&amp;lt; n;
    i &amp;#61; n;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(i !&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i % &lt;span class="hljs-number"&gt;10&lt;/span&gt; % &lt;span class="hljs-number"&gt;2&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
            b &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        } &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; {
            c &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        }
        i/&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;;
