&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;swap&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; *a, &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; *b)&lt;/span&gt;
