&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-title"&gt;lcm&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; a, &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; b)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; || b &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (a &amp;lt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
