&lt;pre&gt;&lt;code&gt;package main

import (
    "fmt"
    "unsafe"
)

func main() {
    fmt.Println("Hello, playground")
    var intaddr int = 0x1040c108
    var addr uintptr = uintptr(intaddr)
    ptr := unsafe.Pointer(addr)
    p := (*int)(ptr)
    fmt.Printf("Type: %T, Ptr: %v, Val: %d", p, p, *p)
}
