&lt;p&gt;下面是我些的代码和运行结果&amp;#xff1a;&lt;/p&gt;

&lt;pre&gt;
&lt;code class="language-cpp"&gt;// 由计算机随机产生10个0~100之间的整数存入数组a中&amp;#xff0c;并将这些数从小到大排序&amp;#xff0c;然后输入一个整数x&amp;#xff0c;在数组a中查找x&amp;#xff0c;若找到则输出相应的下标&amp;#xff0c;否则输出“未找到”。 
#include &amp;lt;stdlib.h&amp;gt;
#include &amp;lt;stdio.h&amp;gt;

#define MAX 100 
#define N 10

int a[N];
int count &amp;#61; N;

void populate() 
{
    for (int i &amp;#61; 0; i &amp;lt; count; &amp;#43;&amp;#43;i)
    {
        a[i] &amp;#61; rand() % (MAX &amp;#43; 1);
    }
}

// 具体要求&amp;#xff1a; (1&amp;#xff09;用选择排序法或冒泡排序法进行排序。 
void bubble_sort()
{
    for (int i &amp;#61; 0; i &amp;lt; count - 1; &amp;#43;&amp;#43;i)
        for (int j &amp;#61; i &amp;#43; 1; j &amp;lt; count; &amp;#43;&amp;#43;j)
            if (a[i] &amp;gt; a[j])
            {
                int temp &amp;#61; a[i];
                a[i] &amp;#61; a[j];
                a[j] &amp;#61; temp;
            }
}

// (2)用顺序查找法或折半查找法进行查找。 
int find(int x)
{
    for (int i &amp;#61; 0; i &amp;lt; count; &amp;#43;&amp;#43;i)
        if (a[i] &amp;#61;&amp;#61; x)
            return i;

    return -1;
}


// (3)如果要删除查找到的元素值&amp;#xff0c;如何修改程序?
int remove(int x)
{
    for (int i &amp;#61; 0; i &amp;lt; count; &amp;#43;&amp;#43;i)
        if (a[i] &amp;#61;&amp;#61; x)
        {
            for (int j &amp;#61; i &amp;#43; 1; j &amp;lt; count; &amp;#43;&amp;#43;j)
                a[j - 1] &amp;#61; a[j];
            --count;    
            return i;
        }

    return -1;
}

// 主函数一次调用前面的函数
int main()
{
    int x;
    int index;

    populate();
    bubble_sort();

    printf(&amp;#34;Here are the %d numbers:\n&amp;#34;, count);
    for (int i &amp;#61; 0; i &amp;lt; count; &amp;#43;&amp;#43;i)
        printf(&amp;#34;%d &amp;#34;, a[i]);
    printf(&amp;#34;\n&amp;#34;);

    printf(&amp;#34;Please enter a number (0 .. 100) to look for and press ENTER: &amp;#34;);
    scanf(&amp;#34;%d&amp;#34;, &amp;amp;x);
    index &amp;#61; find(x);
    if (index !&amp;#61; -1)
        printf(&amp;#34;Found it! Its index (0-based) is %d\n&amp;#34;, index);
    else
        printf(&amp;#34;Not found.\n&amp;#34;);

    printf(&amp;#34;Please enter a number (0 .. 100) to look for and press ENTER: &amp;#34;);
    scanf(&amp;#34;%d&amp;#34;, &amp;amp;x);
    index &amp;#61; find(x);
    if (index !&amp;#61; -1)
        printf(&amp;#34;Found it! Its index (0-based) is %d\n&amp;#34;, index);
    else
        printf(&amp;#34;Not found.\n&amp;#34;);

    printf(&amp;#34;Please enter a number (0 .. 100) to delete and press ENTER: &amp;#34;);
    scanf(&amp;#34;%d&amp;#34;, &amp;amp;x);
    index &amp;#61; remove(x);
    if (index !&amp;#61; -1)
        printf(&amp;#34;Deleted it! Its index (0-based) was %d\n&amp;#34;, index);
    else
        printf(&amp;#34;Not found.\n&amp;#34;);


    printf(&amp;#34;Here are the %d numbers:\n&amp;#34;, count);
    for (int i &amp;#61; 0; i &amp;lt; count; &amp;#43;&amp;#43;i)
        printf(&amp;#34;%d &amp;#34;, a[i]);
    printf(&amp;#34;\n&amp;#34;);
}


