        temp &amp;#61; b;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
        temp &amp;#61; a;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (temp &amp;lt; c)
        temp &amp;#61; c;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; i &amp;#61; temp; i &amp;lt;&amp;#61;a*b*c; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (i % a &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
