                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (i % c &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;)
                {
                    &lt;span class="hljs-keyword"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;最小公倍数&amp;#xff1a;%d&amp;#34;&lt;/span&gt;, i);
                    &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
                }

    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
