```
 #include &lt;stdio.h&gt;
void s(int *a,int *b)
{
	int t = *a;
	*a=*b;
	*b=t;
}
int main()
{
	printf("enter 2 integers:\n");
	int a; int b;
	scanf("%d %d", &amp;a, &amp;b);
	printf("you entered\n%d,%d\n",a,b);
	s(&amp;a,&amp;b);
	printf("after swapping\n%d,%d\n",a,b);
}
