&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;

&lt;span class="hljs-comment"&gt;//带正负号的差&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;cha1&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; a-b;
}

&lt;span class="hljs-comment"&gt;//绝对值差&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;cha&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; a,&lt;span class="hljs-type"&gt;int&lt;/span&gt; b)&lt;/span&gt;
&lt;/span&gt;{
