&lt;p&gt;你题目的解答代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;#include &amp;lt;stdio.h&amp;gt;
#include &amp;lt;stdlib.h&amp;gt;
#include &amp;lt;assert.h&amp;gt;
#include &amp;lt;&lt;span class="hljs-built_in"&gt;time&lt;/span&gt;.h&amp;gt;

#define TREE_TYPE int
#define size &lt;span class="hljs-number"&gt;20&lt;/span&gt;

&lt;span class="hljs-comment"&gt;/**定义树形结构*/&lt;/span&gt;
typedef struct Tree
{
    TREE_TYPE value;
    struct Tree *left;
    struct Tree *right;
}Tree_node;

&lt;span class="hljs-comment"&gt;/**根节点做为全局静态变量*/&lt;/span&gt;
static Tree_node *root;

&lt;span class="hljs-comment"&gt;/**初始化树*/&lt;/span&gt;
void create_tree()
{
    root &amp;#61; NULL;
}

&lt;span class="hljs-comment"&gt;/**向树种插入数据*/&lt;/span&gt;
Tree_node* insert(Tree_node *node,TREE_TYPE value)
{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(node &amp;#61;&amp;#61; NULL)
    {
        node &amp;#61; (Tree_node*)malloc(sizeof(Tree_node) * &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;node&lt;/span&gt;-&amp;gt;&lt;/span&gt; value &amp;#61; value;
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;node&lt;/span&gt; -&amp;gt;&lt;/span&gt; left &amp;#61; NULL;
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;node&lt;/span&gt; -&amp;gt;&lt;/span&gt; right &amp;#61; NULL;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(root &amp;#61;&amp;#61; NULL) &lt;span class="hljs-comment"&gt;//若是为根节点&lt;/span&gt;
            root &amp;#61; node;
    }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
    {
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;if&lt;/span&gt;(value &amp;lt; node-&amp;gt;&lt;/span&gt;value) &lt;span class="hljs-comment"&gt;//比该节点值小&amp;#xff0c;写向左子树&lt;/span&gt;
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;node&lt;/span&gt;-&amp;gt;&lt;/span&gt;&lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;left&lt;/span&gt; &amp;#61; insert(node-&amp;gt;&lt;/span&gt;left,value);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-comment"&gt;//比该节点值大&amp;#xff0c;写向右子树&lt;/span&gt;
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;node&lt;/span&gt;-&amp;gt;&lt;/span&gt;&lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;right&lt;/span&gt; &amp;#61; insert(node-&amp;gt;&lt;/span&gt;right,value);
    }
    return node;
}

&lt;span class="hljs-comment"&gt;/**显示树中全部节点内容*/&lt;/span&gt;
void show_all(Tree_node *node)
{
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(node !&amp;#61; NULL)
    {
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;if&lt;/span&gt;(node-&amp;gt;&lt;/span&gt;left !&amp;#61; NULL)
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;show_all&lt;/span&gt;(node-&amp;gt;&lt;/span&gt;left);
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;printf&lt;/span&gt;(&amp;#34;%d &amp;#34;,node-&amp;gt;&lt;/span&gt;value);
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;if&lt;/span&gt;(node-&amp;gt;&lt;/span&gt;right !&amp;#61; NULL);
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;show_all&lt;/span&gt;(node-&amp;gt;&lt;/span&gt;right);
    }
}

&lt;span class="hljs-comment"&gt;/**根据值查找某一特定节点*/&lt;/span&gt;
Tree_node* search(Tree_node *node,TREE_TYPE value)
{
    Tree_node *result &amp;#61; NULL;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(node !&amp;#61; NULL)
    {
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;if&lt;/span&gt;(value &amp;#61;&amp;#61; node-&amp;gt;&lt;/span&gt;value)
            result &amp;#61; node;
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(value &amp;lt; node-&amp;gt;&lt;/span&gt;value)
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;result&lt;/span&gt; &amp;#61; search(node-&amp;gt;&lt;/span&gt;left,value);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;result&lt;/span&gt; &amp;#61; search(node-&amp;gt;&lt;/span&gt;right,value);
    }
    return result;
}

&lt;span class="hljs-comment"&gt;/**找到某一值对应节点的父节点*/&lt;/span&gt;
Tree_node* find_parent(Tree_node *parent,TREE_TYPE value)
{
    Tree_node *result &amp;#61; NULL;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(parent !&amp;#61; NULL)
    {
        &lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;if&lt;/span&gt;(value &amp;lt; parent-&amp;gt;&lt;/span&gt;&lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;value&lt;/span&gt; &amp;amp;&amp;amp; parent-&amp;gt;&lt;/span&gt;left !&amp;#61; NULL) &lt;span class="hljs-comment"&gt;//比父节点值小&amp;#xff0c;访问左子树&lt;/span&gt;
