void sort(int *a, int n)
{

	int i, j, temp;
	for (i = 0; i &lt; n - 1; i++)
	{
		for (j = 0; j&lt;n - 1 - i; j++)
		{
			if (a[j]&gt;a[j + 1])
			{
				temp = a[j];
				a[j] = a[j + 1];
				a[j + 1] = temp;
			}
		}
	}
}
