void sort(int *p,int n)
{
    int i,j,temp;
    for (j=0;j<n-1;j++)
      for (i=0;i<n-1-j;i++)
        if (*(p+i)>*(p+i+1))
          {
            temp = *p;
            *p = *(p+1);
            *(p+1) = temp;
          }
}

