            &lt;span class="hljs-keyword"&gt;elseif&lt;/span&gt;(&lt;span class="hljs-variable"&gt;$i&lt;/span&gt; &amp;lt; (strlen(&lt;span class="hljs-variable"&gt;$str&lt;/span&gt;)-&lt;span class="hljs-number"&gt;1&lt;/span&gt;) &amp;amp;&amp;amp; &lt;span class="hljs-variable"&gt;$str&lt;/span&gt;[&lt;span class="hljs-variable"&gt;$i&lt;/span&gt;&amp;#43;&lt;span class="hljs-number"&gt;1&lt;/span&gt;] !&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;) {
