#include &lt;stdio.h&gt;
#include &lt;string.h&gt;

const int M=100005;

bool prime[M];

void get_prime()
{
	memset(prime,true,sizeof(prime));
	prime[1]=false;
	for(int i=2;i&lt;M;i++)
		if(prime[i])
			for(int j=2;j*i&lt;M;j++)
				prime[i*j]=false;
}

void deal(int x)
{
	for(int i=2;i&lt;=x/2;i++)
		if(prime[i]&amp;&amp;prime[x-i]){
			printf("%d: 1:%d 2:%d\n",x,i,x-i);
			return ;
		}
}

int main()
{
	get_prime();
	int x,y;
	while(~scanf("%d%d",&amp;x,&amp;y)){
		for(int i=x%2? x+1:x;i&lt;=y;i+=2)
			deal(i);
	}
	return 0;
}

 
