    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (&lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; &lt;span class="hljs-number"&gt;10&lt;/span&gt;; i&amp;#43;&amp;#43;) {
