#include &lt;iostream&gt;
using namespace std;

template &lt;typename F&gt;
int count(int * a, int * b, int na, int nb, F const&amp; pred) {
	int x = 0;
	for (int i = 0; i &lt; na; i++)
		for (int j = 0; j &lt; nb; j++)
			if (pred(a[i], b[j])) x++;
	return x;
}

int main() {

	int a[3] = { 1, 3, 31 };
	int b[2] = { 13, 2 };
    int n1 = count(a, b, 3, 2, [](int a, int b) -&gt; int {
        return a - b == 1 || a - b == -1;
    });
    int n2 = count(a, b, 3, 2, [](int a, int b) -&gt; int {
        return a % 10 == b / 10 &amp;&amp; a / 10 == b % 10;
    });
	cout &lt;&lt; n1 &lt;&lt; endl;
	cout &lt;&lt; n2 &lt;&lt; endl;
}
