h &amp;#61; &lt;span class="hljs-built_in"&gt;float&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;input&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入梯形的高:&amp;#34;&lt;/span&gt;))
s &amp;#61; &lt;span class="hljs-built_in"&gt;S&lt;/span&gt;(a,b,h)
&lt;span class="hljs-built_in"&gt;print&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#39;梯形面积&amp;#xff1a;%0.2f&amp;#39;&lt;/span&gt;%s)
