&lt;p&gt;完整代码为&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; x,y;
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;x);
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(x&amp;lt;&lt;span class="hljs-number"&gt;1&lt;/span&gt;)
    {
        y&amp;#61;x;
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;y&amp;#61;x&amp;#61;%d&amp;#34;&lt;/span&gt;,y);    
    }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(x&amp;lt;&lt;span class="hljs-number"&gt;10&lt;/span&gt;)
    {
        y&amp;#61;&lt;span class="hljs-number"&gt;2&lt;/span&gt;*x&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;y&amp;#61;2x-1&amp;#61;%d&amp;#34;&lt;/span&gt;,y);
    }
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt; &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(x&amp;gt;&amp;#61;&lt;span class="hljs-number"&gt;10&lt;/span&gt;)
    {
        y&amp;#61;&lt;span class="hljs-number"&gt;3&lt;/span&gt;*x&lt;span class="hljs-number"&gt;-11&lt;/span&gt;;
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;y&amp;#61;3x-11&amp;#61;%d&amp;#34;&lt;/span&gt;,y);
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; N 5&lt;/span&gt;

&lt;span class="hljs-keyword"&gt;struct&lt;/span&gt; &lt;span class="hljs-title class_"&gt;student&lt;/span&gt;
{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; num;
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; name[&lt;span class="hljs-number"&gt;10&lt;/span&gt;];
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; course1;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; course2;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; course3;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; total;
};     &lt;span class="hljs-comment"&gt;//定义结构体类型&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入学生信息&amp;#xff0c;以空格隔开\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;学号 数学 语文 英语 姓名\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;struct&lt;/span&gt; &lt;span class="hljs-title class_"&gt;student&lt;/span&gt; st[N];     &lt;span class="hljs-comment"&gt;//定义结构体变量&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i, max, maxi;
    &lt;span class="hljs-type"&gt;float&lt;/span&gt; average1 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;,average2 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;,average3 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; N; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;st[i].num);
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;st[i].course1);
        average1 &amp;#61; average1 &amp;#43; st[i].course1;
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;st[i].course2);
        average2 &amp;#61; average2 &amp;#43; st[i].course2;
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;st[i].course3);
        average3 &amp;#61; average3 &amp;#43; st[i].course3;
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,st[i].name);
        st[i].total &amp;#61; st[i].course1 &amp;#43; st[i].course2 &amp;#43; st[i].course3;     &lt;span class="hljs-comment"&gt;//求出当前学生的总分&lt;/span&gt;
    }
    average1 &amp;#61; average1 / N;
    average2 &amp;#61; average2 / N;
    average3 &amp;#61; average3 / N;     &lt;span class="hljs-comment"&gt;//求出各科平均分&lt;/span&gt;
    max &amp;#61; st[&lt;span class="hljs-number"&gt;0&lt;/span&gt;].total;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; N; i&amp;#43;&amp;#43;)
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(st[i].total &amp;gt;&amp;#61; max)
        {
            max &amp;#61; st[i].total;
            maxi &amp;#61; i;
        }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;三门课的平均成绩:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;数学 语文 英语\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%.2f %.2f %.2f\n&amp;#34;&lt;/span&gt;,average1,average2,average3);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;学号 姓名 平均分\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; i &amp;lt; N; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d   %s  %.2f\n&amp;#34;&lt;/span&gt; , st[i].num , st[i].name , st[i].total/&lt;span class="hljs-number"&gt;3.0&lt;/span&gt;);

    }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;总分最高的学生:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;学号   姓名   课程1   课程2   课程3  总分\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d %s %d %d %d %d\n&amp;#34;&lt;/span&gt; , st[maxi].num , st[maxi].name , st[maxi].course1 , st[maxi].course2 , st[maxi].course3 , st[maxi].total);
}
&lt;/code&gt;&lt;/pre&gt;
