&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;string&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt; &lt;/span&gt;{
    &lt;span class="hljs-comment"&gt;// 从键盘输入原始文本和待查找的字符串&lt;/span&gt;
    string text, &lt;span class="hljs-type"&gt;word&lt;/span&gt;;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;请输入一段文本&amp;#xff1a;&amp;#34;&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;getline&lt;/span&gt;(cin, text);
    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;请输入待查找的字符串&amp;#xff1a;&amp;#34;&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;getline&lt;/span&gt;(cin, &lt;span class="hljs-type"&gt;word&lt;/span&gt;);

    &lt;span class="hljs-comment"&gt;// 统计字符串出现次数和位置&lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; count &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;size_t&lt;/span&gt; pos &amp;#61; text.&lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(&lt;span class="hljs-type"&gt;word&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt; (pos !&amp;#61; string::npos) {
        count&amp;#43;&amp;#43;;
        cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;第 &amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; count &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; 个 \&amp;#34;&amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-type"&gt;word&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;\&amp;#34; 在文本中出现的位置是&amp;#xff1a; &amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; pos &amp;lt;&amp;lt; endl;
        pos &amp;#61; text.&lt;span class="hljs-built_in"&gt;find&lt;/span&gt;(&lt;span class="hljs-type"&gt;word&lt;/span&gt;, pos &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;);
    }

    &lt;span class="hljs-comment"&gt;// 输出结果&lt;/span&gt;
    cout &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;\&amp;#34;&amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-type"&gt;word&lt;/span&gt; &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34;\&amp;#34; 在文本中出现了 &amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; count &amp;lt;&amp;lt; &lt;span class="hljs-string"&gt;&amp;#34; 次。&amp;#34;&lt;/span&gt; &amp;lt;&amp;lt; endl;

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;程序运行时&amp;#xff0c;会提示用户输入一段文本和待查找的字符串。然后程序会使用&lt;code class="language-javascript"&gt;string::find()&lt;/code&gt;函数在文本中查找待查找的字符串&amp;#xff0c;并统计其出现次数和位置。最后程序会输出结果&amp;#xff0c;显示待查找的字符串在原始文本中出现的次数和位置。&lt;/p&gt;
&lt;p&gt;需要注意的是&amp;#xff0c;&lt;code class="language-javascript"&gt;string::find()&lt;/code&gt;函数会返回第一个匹配子串的位置&amp;#xff0c;如果没有找到则返回&lt;code class="language-javascript"&gt;string::npos&lt;/code&gt;。因此&amp;#xff0c;在找到第一个匹配子串的位置之后&amp;#xff0c;需要通过第二个参数指定查找的开始位置&amp;#xff0c;以便继续查找下一个匹配子串。&lt;/p&gt;

&lt;p&gt;代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-comment"&gt;//转2进制字符串&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;fun&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;char&lt;/span&gt; ch,&lt;span class="hljs-type"&gt;char&lt;/span&gt; buf[])&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,t,n&amp;#61;&lt;span class="hljs-number"&gt;7&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-number"&gt;8&lt;/span&gt;;i&amp;#43;&amp;#43;)
        buf[i] &amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;0&amp;#39;&lt;/span&gt;;
    t &amp;#61; ch;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(t)
    {
        buf[n] &amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;0&amp;#39;&lt;/span&gt;&amp;#43;t%&lt;span class="hljs-number"&gt;2&lt;/span&gt;;
        t/&amp;#61;&lt;span class="hljs-number"&gt;2&lt;/span&gt;;
        n--;
    }
    buf[&lt;span class="hljs-number"&gt;8&lt;/span&gt;] &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; buf[&lt;span class="hljs-number"&gt;1000&lt;/span&gt;]&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;};
    &lt;span class="hljs-type"&gt;char&lt;/span&gt; tmp[&lt;span class="hljs-number"&gt;10&lt;/span&gt;]&amp;#61;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;};
    &lt;span class="hljs-built_in"&gt;gets&lt;/span&gt;(buf);
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(buf[i])
    {
        &lt;span class="hljs-built_in"&gt;fun&lt;/span&gt;(buf[i],tmp);
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;)
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%s&amp;#34;&lt;/span&gt;,tmp);
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
            &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34; %s&amp;#34;&lt;/span&gt;,tmp);
        i&amp;#43;&amp;#43;;
    }
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n&amp;#34;&lt;/span&gt;);
    i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(buf[i])
    {
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%0x&amp;#34;&lt;/span&gt;,buf[i]);
        i&amp;#43;&amp;#43;;
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

