    &lt;span class="hljs-type"&gt;int&lt;/span&gt; sum &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Input n(n &amp;lt;&amp;#61; 500):&amp;#34;&lt;/span&gt;);
