    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (x &amp;#61; n, count &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, sum &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;; count &amp;lt; &lt;span class="hljs-number"&gt;10&lt;/span&gt; &amp;amp;&amp;amp; x &amp;gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; x--)  &lt;span class="hljs-comment"&gt;//for (x &amp;#61; 0; x &amp;lt;&amp;#61; n; x&amp;#43;&amp;#43;)&lt;/span&gt;
