&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;CalScore&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;float&lt;/span&gt; score[], &lt;span class="hljs-type"&gt;int&lt;/span&gt; n)&lt;/span&gt; &lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-type"&gt;float&lt;/span&gt; sum &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    MaxScore &amp;#61; score[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
    MinScore &amp;#61; score[&lt;span class="hljs-number"&gt;0&lt;/span&gt;];
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;; i &amp;lt; n; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (MaxScore &amp;lt; score[i])
            MaxScore &amp;#61; score[i];
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; (MinScore &amp;gt; score[i])
            MinScore &amp;#61; score[i];
        sum &amp;#43;&amp;#61; score[i];
    }
    AverScore &amp;#61; sum / n;
}
&lt;/code&gt;&lt;/pre&gt;
