&lt;pre&gt;&lt;code class="language-javascript"&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; N 3&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; M 4&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;myFinder&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; nData[][&lt;span class="hljs-number"&gt;4&lt;/span&gt;], &lt;span class="hljs-type"&gt;int&lt;/span&gt; *pMax, &lt;span class="hljs-type"&gt;int&lt;/span&gt; *pMin)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; i&amp;lt;N ; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; j&amp;lt;M ; j&amp;#43;&amp;#43;)
        {            
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*pMax&amp;lt;nData[i][j])*pMax&amp;#61;nData[i][j];
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(*pMin&amp;gt;nData[i][j])*pMin&amp;#61;nData[i][j];
        }
    }
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[N][M]&amp;#61; {&lt;!-- --&gt;{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;34&lt;/span&gt;,&lt;span class="hljs-number"&gt;27&lt;/span&gt;,&lt;span class="hljs-number"&gt;16&lt;/span&gt;,&lt;span class="hljs-number"&gt;0&lt;/span&gt;},{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;-5&lt;/span&gt;,&lt;span class="hljs-number"&gt;12&lt;/span&gt;,&lt;span class="hljs-number"&gt;78&lt;/span&gt;,&lt;span class="hljs-number"&gt;9&lt;/span&gt;},{&lt;!-- --&gt;&lt;span class="hljs-number"&gt;-9&lt;/span&gt;,&lt;span class="hljs-number"&gt;7&lt;/span&gt;,&lt;span class="hljs-number"&gt;45&lt;/span&gt;,&lt;span class="hljs-number"&gt;6&lt;/span&gt;}};
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,j,max,min;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入3行4列的整型数据&amp;#xff1a;\n&amp;#34;&lt;/span&gt;);

    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; i&amp;lt;N ; i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;; j&amp;lt;M ; j&amp;#43;&amp;#43;)
        {
            &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;a[i][j]);
        }
    }

    &lt;span class="hljs-built_in"&gt;myFinder&lt;/span&gt;(a,&amp;amp;max,&amp;amp;min);

    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n最大值是&amp;#xff1a;%d\n&amp;#34;&lt;/span&gt;,max);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;最小值是&amp;#xff1a;%d\n &amp;#34;&lt;/span&gt;,min);

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;div class="post-text" itemprop="text"&gt;
&lt;p&gt;It's a work-in-progress learning exercise, and certainly a poor example of good style, but &lt;a href="https://web.archive.org/web/20100111071907/http://svn.logic.net/trac/esm/browser/languages/Go/server.go" rel="nofollow noreferrer"&gt;here you go&lt;/a&gt; (&lt;a href="http://svn.logic.net/trac/esm/browser/languages/spec.html" rel="nofollow noreferrer"&gt;spec&lt;/a&gt;).&lt;/p&gt;

&lt;p&gt;Additionally, as a more exotic example, I made &lt;a href="http://groups.google.com/group/golang-nuts/msg/cca70b4f083bd319" rel="nofollow noreferrer"&gt;a post&lt;/a&gt; on the go-nuts mailing list regarding using interface{} for building functions that work with anonymous data (in this case, a "ternary operation" function):&lt;/p&gt;

&lt;pre&gt;&lt;code&gt;package main
import "fmt";
func Tern(exp bool, a interface{}, b interface{}) (interface{}) {
    if exp { return a }
    return b
}
func main() {
    a := 7; b := 1;
    result := Tern(a &amp;gt; b, a, b);
    fmt.Printf("%d
", result);
}
&lt;/code&gt;&lt;/pre&gt;
    &lt;/div&gt;

&lt;p&gt;完整代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;iostream&amp;gt;&lt;/span&gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; std;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; LIMIT 100000000&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;factorial&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; &lt;span class="hljs-type"&gt;long&lt;/span&gt; fact &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;static&lt;/span&gt; &lt;span class="hljs-type"&gt;int&lt;/span&gt; times &amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
        fact*&amp;#61;times;
    times&amp;#43;&amp;#43;;
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(fact&amp;lt;LIMIT)
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;1&lt;/span&gt;;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;
    {
        cout&amp;lt;&amp;lt;fact&amp;lt;&amp;lt;&lt;span class="hljs-string"&gt;&amp;#34; &amp;#34;&lt;/span&gt;&amp;lt;&amp;lt;times&lt;span class="hljs-number"&gt;-1&lt;/span&gt;&amp;lt;&amp;lt;endl;
        &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    }
}
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,n;
    cin&amp;gt;&amp;gt;n;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;)
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(!&lt;span class="hljs-built_in"&gt;factorial&lt;/span&gt;())
            &lt;span class="hljs-keyword"&gt;break&lt;/span&gt;;
    }
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;参考GPT和自己的思路&amp;#xff1a;&lt;/p&gt;
&lt;p&gt;您的代码中出现了几个问题&amp;#xff1a;&lt;/p&gt;
&lt;ol&gt;&lt;li&gt;&lt;p&gt;在第二行中&amp;#xff0c;您使用了 &lt;code class="language-javascript"&gt;input()&lt;/code&gt; 函数获取用户输入&amp;#xff0c;并使用 &lt;code class="language-javascript"&gt;split()&lt;/code&gt; 方法将输入的年份和月份分开。但是您没有将输入的字符串转换成数字类型。因此&amp;#xff0c;在第 9 行判断是否是闰年时&amp;#xff0c;会将年份作为字符串进行计算&amp;#xff0c;而不是数字。因此&amp;#xff0c;需要在第二行使用 &lt;code class="language-javascript"&gt;map()&lt;/code&gt; 函数&amp;#xff0c;将两个字符串转换为数字。&lt;/p&gt;
&lt;/li&gt;&lt;li&gt;&lt;p&gt;在第 4 行和第 6 行中&amp;#xff0c;您使用了月份作为元素的列表。由于用户输入的月份是字符串类型&amp;#xff0c;因此需要将其转换为整数类型进行比较。因此&amp;#xff0c;需要将列表中的元素都改为整数类型。&lt;/p&gt;
&lt;/li&gt;&lt;/ol&gt;
&lt;p&gt;下面是修改后的代码&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-keyword"&gt;if&lt;/span&gt; __name__ &amp;#61;&amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;__main__&amp;#39;&lt;/span&gt;:
    &lt;span class="hljs-built_in"&gt;year&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;month&lt;/span&gt; &amp;#61; &lt;span class="hljs-built_in"&gt;map&lt;/span&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt;, &lt;span class="hljs-title function_"&gt;input&lt;/span&gt;().&lt;span class="hljs-property"&gt;split&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#39;,&amp;#39;&lt;/span&gt;))
    # 判断
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-built_in"&gt;month&lt;/span&gt; in [&lt;span class="hljs-number"&gt;1&lt;/span&gt;, &lt;span class="hljs-number"&gt;3&lt;/span&gt;, &lt;span class="hljs-number"&gt;5&lt;/span&gt;, &lt;span class="hljs-number"&gt;7&lt;/span&gt;, &lt;span class="hljs-number"&gt;8&lt;/span&gt;, &lt;span class="hljs-number"&gt;10&lt;/span&gt;, &lt;span class="hljs-number"&gt;12&lt;/span&gt;]:
        days &amp;#61; &lt;span class="hljs-number"&gt;31&lt;/span&gt;
    elif &lt;span class="hljs-built_in"&gt;month&lt;/span&gt; in [&lt;span class="hljs-number"&gt;4&lt;/span&gt;, &lt;span class="hljs-number"&gt;6&lt;/span&gt;, &lt;span class="hljs-number"&gt;9&lt;/span&gt;, &lt;span class="hljs-number"&gt;11&lt;/span&gt;]:
        days &amp;#61; &lt;span class="hljs-number"&gt;30&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-built_in"&gt;year&lt;/span&gt; % &lt;span class="hljs-number"&gt;400&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; &lt;span class="hljs-title function_"&gt;or&lt;/span&gt; (&lt;span class="hljs-built_in"&gt;year&lt;/span&gt; % &lt;span class="hljs-number"&gt;4&lt;/span&gt; &amp;#61;&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt; and &lt;span class="hljs-built_in"&gt;year&lt;/span&gt; % &lt;span class="hljs-number"&gt;100&lt;/span&gt; !&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;):
            days &amp;#61; &lt;span class="hljs-number"&gt;29&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
            days &amp;#61; &lt;span class="hljs-number"&gt;28&lt;/span&gt;
    # 输出
    &lt;span class="hljs-built_in"&gt;print&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#39;%d&amp;#39;&lt;/span&gt; % (days))
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;define&lt;/span&gt; N 10&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;Inverse&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *pData, &lt;span class="hljs-type"&gt;int&lt;/span&gt; nLen)&lt;/span&gt;&lt;/span&gt;;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[N],i;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;请输入10个数&amp;#xff1a;&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;N ;i&amp;#43;&amp;#43; )
        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;a[i]);
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n源数组序列是&amp;#xff1a;&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;N ;i&amp;#43;&amp;#43; )
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%4d&amp;#34;&lt;/span&gt;,a[i]);

    &lt;span class="hljs-built_in"&gt;Inverse&lt;/span&gt;(a,N);

    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;\n逆序后数组序列是&amp;#xff1a;&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;N ;i&amp;#43;&amp;#43; )
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%4d&amp;#34;&lt;/span&gt;,a[i]);


    &lt;span class="hljs-built_in"&gt;system&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;pause&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;void&lt;/span&gt; &lt;span class="hljs-title"&gt;Inverse&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;int&lt;/span&gt; *pData, &lt;span class="hljs-type"&gt;int&lt;/span&gt; nLen)&lt;/span&gt;
&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,t;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; (i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;nLen/&lt;span class="hljs-number"&gt;2&lt;/span&gt;;i&amp;#43;&amp;#43;)
    {
        t&amp;#61;pData[i];
        pData[i]&amp;#61;pData[nLen-i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;];
        pData[nLen-i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;]&amp;#61;t;
    }    
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-python"&gt;s&amp;#61;&lt;span class="hljs-selector-tag"&gt;input&lt;/span&gt;()
cnt&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt; &lt;span class="hljs-selector-tag"&gt;i&lt;/span&gt; &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; s:
    &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; &lt;span class="hljs-selector-tag"&gt;i&lt;/span&gt;&lt;span class="hljs-selector-class"&gt;.isalpha&lt;/span&gt;() and not &lt;span class="hljs-selector-tag"&gt;i&lt;/span&gt;&lt;span class="hljs-selector-class"&gt;.isdigit&lt;/span&gt;():
        cnt&amp;#43;&amp;#61;&lt;span class="hljs-number"&gt;1&lt;/span&gt;
&lt;span class="hljs-function"&gt;&lt;span class="hljs-title"&gt;print&lt;/span&gt;&lt;span class="hljs-params"&gt;(len(s)&lt;/span&gt;&lt;/span&gt;-cnt)
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;&lt;br /&gt; &lt;strong&gt;有帮助请&lt;font color="red"&gt;&lt;strong&gt;采纳&lt;/strong&gt;&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;

&lt;p&gt;可以借助&lt;strong&gt;Manacher&amp;#39;s Algorithm&amp;#xff08;马拉车算法&amp;#xff09;&lt;/strong&gt;进行求解&lt;br /&gt;这个算法是以每一个字符为中心, 向两边发散,同时,用一个数组p来记录以每一个字符为中心的回文串的一半的长度.&lt;br /&gt;具体的可网上检索进行了解&lt;br /&gt;代码如下&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-python"&gt;class Solution:
    def longestPalindrome(self, s):
        def get_length(&lt;span class="hljs-keyword"&gt;string&lt;/span&gt;, index):
            &lt;span class="hljs-comment"&gt;# 借助循环求出index为中心的最长回文字串&lt;/span&gt;
            &lt;span class="hljs-built_in"&gt;start&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;length&lt;/span&gt; &amp;#61; index&lt;span class="hljs-comment"&gt; // 2, 0&lt;/span&gt;
           &lt;span class="hljs-comment"&gt;#获取字符串长度&amp;#xff0c;并赋值非r_&lt;/span&gt;
            r_ &amp;#61; &lt;span class="hljs-built_in"&gt;len&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;string&lt;/span&gt;)
            &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; i &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; range(&lt;span class="hljs-number"&gt;1&lt;/span&gt;, index &amp;#43; &lt;span class="hljs-number"&gt;1&lt;/span&gt;):
                &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; index &amp;#43; i &amp;lt; r_ &lt;span class="hljs-keyword"&gt;and&lt;/span&gt; &lt;span class="hljs-keyword"&gt;string&lt;/span&gt;[index - i] &amp;#61;&amp;#61; &lt;span class="hljs-keyword"&gt;string&lt;/span&gt;[index &amp;#43; i]:
                    &lt;span class="hljs-built_in"&gt;start&lt;/span&gt; &amp;#61; (index - i)&lt;span class="hljs-comment"&gt; // 2&lt;/span&gt;
                    &lt;span class="hljs-built_in"&gt;length&lt;/span&gt; &amp;#43;&amp;#61; &lt;span class="hljs-number"&gt;1&lt;/span&gt;
                &lt;span class="hljs-keyword"&gt;else&lt;/span&gt;:
                    break
            &lt;span class="hljs-literal"&gt;return&lt;/span&gt; &lt;span class="hljs-built_in"&gt;start&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;length&lt;/span&gt;

        s_list &amp;#61; [c &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; c &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; s]

        &lt;span class="hljs-comment"&gt;# 形成新串&lt;/span&gt;
        new_s &amp;#61; &lt;span class="hljs-string"&gt;&amp;#39;#&amp;#39;&lt;/span&gt; &amp;#43; &lt;span class="hljs-string"&gt;&amp;#39;#&amp;#39;&lt;/span&gt;.join(s_list) &amp;#43; &lt;span class="hljs-string"&gt;&amp;#39;#&amp;#39;&lt;/span&gt;        

        &lt;span class="hljs-comment"&gt;#初始化赋值&lt;/span&gt;
        max_start &amp;#61; max_length &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;
        &lt;span class="hljs-built_in"&gt;length&lt;/span&gt; &amp;#61; &lt;span class="hljs-built_in"&gt;len&lt;/span&gt;(new_s)
        &lt;span class="hljs-keyword"&gt;for&lt;/span&gt; index &lt;span class="hljs-keyword"&gt;in&lt;/span&gt; range(&lt;span class="hljs-number"&gt;0&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;length&lt;/span&gt;):
            &lt;span class="hljs-built_in"&gt;start&lt;/span&gt;, r_length &amp;#61; get_length(new_s, index)
            &lt;span class="hljs-keyword"&gt;if&lt;/span&gt; max_length &amp;lt; r_length:            &lt;span class="hljs-comment"&gt;#如果max_length小于r_length 则将start、r_length分别赋值给max_start、max_length&lt;/span&gt;
                max_start, max_length &amp;#61; &lt;span class="hljs-built_in"&gt;start&lt;/span&gt;, r_length
        &lt;span class="hljs-literal"&gt;return&lt;/span&gt; s[max_start: max_start&amp;#43;max_length]

&lt;/code&gt;&lt;/pre&gt;

&lt;p&gt;C&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-javascript"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt;&amp;lt;string.h&amp;gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; main()
{
    char &lt;span class="hljs-keyword"&gt;str&lt;/span&gt;[&lt;span class="hljs-number"&gt;1000&lt;/span&gt;]&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
    gets(&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;)&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;int&lt;/span&gt; len&amp;#61;&lt;span class="hljs-keyword"&gt;strlen&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;),M&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,m&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;&lt;span class="hljs-comment"&gt;; i&amp;lt;len; i&amp;#43;&amp;#43;)&lt;/span&gt;
    {
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;[i]&amp;lt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;Z&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;[i]&amp;gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;A&amp;#39;&lt;/span&gt;) M&amp;#43;&amp;#43;&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;[i]&amp;lt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;z&amp;#39;&lt;/span&gt;&amp;amp;&amp;amp;&lt;span class="hljs-keyword"&gt;str&lt;/span&gt;[i]&amp;gt;&amp;#61;&lt;span class="hljs-string"&gt;&amp;#39;a&amp;#39;&lt;/span&gt;) m&amp;#43;&amp;#43;&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
    }
    printf(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,M-m)&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;&lt;span class="hljs-comment"&gt;;&lt;/span&gt;
}
&lt;/code&gt;&lt;/pre&gt;

&lt;pre&gt;&lt;code class="language-c&amp;#43;&amp;#43;"&gt;
&lt;span class="hljs-meta"&gt;#include&amp;lt;iostream&amp;gt;&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;using&lt;/span&gt; &lt;span class="hljs-keyword"&gt;namespace&lt;/span&gt; &lt;span class="hljs-title"&gt;std&lt;/span&gt;;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;(&lt;span class="hljs-params"&gt;&lt;/span&gt;)&lt;/span&gt;{
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; T;
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; sum_85 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;, sum_60 &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    cin &amp;gt;&amp;gt; T;
    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; n &amp;#61; T;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(T--){
        &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; score;
        cin&amp;gt;&amp;gt;score;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(score &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;85&lt;/span&gt;) sum_85 &amp;#43;&amp;#43;;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(score &amp;gt;&amp;#61; &lt;span class="hljs-number"&gt;60&lt;/span&gt;) sum_60 &amp;#43;&amp;#43;;
    }

    printf(&lt;span class="hljs-string"&gt;&amp;#34;%d%%\n%d%%&amp;#34;&lt;/span&gt;, &lt;span class="hljs-built_in"&gt;int&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;float&lt;/span&gt;(sum_60) * &lt;span class="hljs-number"&gt;100&lt;/span&gt; / n &amp;#43; &lt;span class="hljs-number"&gt;0.5&lt;/span&gt;), &lt;span class="hljs-built_in"&gt;int&lt;/span&gt;(&lt;span class="hljs-built_in"&gt;float&lt;/span&gt;(sum_85) * &lt;span class="hljs-number"&gt;100&lt;/span&gt; / n &amp;#43; &lt;span class="hljs-number"&gt;0.5&lt;/span&gt;));
    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}

