}

&lt;span class="hljs-comment"&gt;//获取字符 &lt;/span&gt;
getchar&lt;span class="hljs-constructor"&gt;O(&lt;span class="hljs-params"&gt;char&lt;/span&gt; &lt;span class="hljs-operator"&gt;*&lt;/span&gt; &lt;span class="hljs-params"&gt;ch&lt;/span&gt;)&lt;/span&gt;{

    *ch &amp;#61;getchar&lt;span class="hljs-literal"&gt;()&lt;/span&gt;;


}
&lt;span class="hljs-comment"&gt;//删除函数 &lt;/span&gt;
&lt;span class="hljs-constructor"&gt;Delete(&lt;span class="hljs-params"&gt;char&lt;/span&gt; &lt;span class="hljs-operator"&gt;*&lt;/span&gt; &lt;span class="hljs-params"&gt;str&lt;/span&gt;,&lt;span class="hljs-params"&gt;char&lt;/span&gt; &lt;span class="hljs-params"&gt;ch&lt;/span&gt;)&lt;/span&gt;{


    &lt;span class="hljs-built_in"&gt;int&lt;/span&gt; i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;,j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;

    &lt;span class="hljs-comment"&gt;//循环判断到字符串结尾 &lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;while&lt;/span&gt;(str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;!&amp;#61;&lt;span class="hljs-character"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;){
        &lt;span class="hljs-comment"&gt;//如果字符串当前字符不是要删除的字符就把这个字符依次存入原字符串的前面位置 &lt;/span&gt;
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;!&amp;#61;ch){
            str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&amp;#61;str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;i&lt;/span&gt;]&lt;/span&gt;;
            j&amp;#43;&amp;#43;;  &lt;span class="hljs-comment"&gt;//指向下一个存储非删除字符的位置 &lt;/span&gt;
        }
        i&amp;#43;&amp;#43;;  &lt;span class="hljs-comment"&gt;//指向下一个要判断的字符串位置 &lt;/span&gt;
    }
    &lt;span class="hljs-comment"&gt;//结尾加字符串结束空字符 &lt;/span&gt;
    str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-identifier"&gt;j&lt;/span&gt;]&lt;/span&gt;&amp;#61;&lt;span class="hljs-character"&gt;&amp;#39;\0&amp;#39;&lt;/span&gt;;

}

&lt;span class="hljs-built_in"&gt;int&lt;/span&gt; main(void){

    &lt;span class="hljs-built_in"&gt;char&lt;/span&gt; str&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-number"&gt;20&lt;/span&gt;]&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;char&lt;/span&gt; ch ;
    &lt;span class="hljs-built_in"&gt;char&lt;/span&gt; results&lt;span class="hljs-literal"&gt;[&lt;span class="hljs-number"&gt;20&lt;/span&gt;]&lt;/span&gt;;

    printf(&lt;span class="hljs-string"&gt;&amp;#34;Input a string:\n&amp;#34;&lt;/span&gt;);
    gets&lt;span class="hljs-constructor"&gt;Q(&lt;span class="hljs-params"&gt;str&lt;/span&gt;)&lt;/span&gt;;
    printf(&lt;span class="hljs-string"&gt;&amp;#34;Input a character:\n&amp;#34;&lt;/span&gt;);
    getchar&lt;span class="hljs-constructor"&gt;O(&amp;amp;&lt;span class="hljs-params"&gt;ch&lt;/span&gt;)&lt;/span&gt;;
    &lt;span class="hljs-constructor"&gt;Delete(&lt;span class="hljs-params"&gt;str&lt;/span&gt;,&lt;span class="hljs-params"&gt;ch&lt;/span&gt;)&lt;/span&gt;;
    printf(&lt;span class="hljs-string"&gt;&amp;#34;Results:%s\n&amp;#34;&lt;/span&gt;,str);

    return &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
}
&lt;/code&gt;&lt;/pre&gt;

