java部分

package com.androidtest.drawtablelayout;

import android.app.Activity;
import android.os.Bundle;
import android.widget.TableLayout;
import android.widget.TableLayout.LayoutParams;
import android.widget.TableRow;
import android.widget.TextView;

public class DrawTableLayoutByJavaActivity extends Activity {
    /** Called when the activity is first created. */
    @Override
    public void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.main);
        TableLayout mytable = (TableLayout) findViewById(R.id.mytable);
        int numberOfRow =3;
        int numberOfColumn =3;
        int cellDimension =24;
        int cellPadding =2;
        for (int row = 0;row&lt;numberOfRow; row ++){
        	TableRow tableRow = new TableRow(this);
        	tableRow.setLayoutParams(new LayoutParams((cellDimension+2*cellPadding)*numberOfColumn,cellDimension +2*cellPadding));
        	for (int column =0;column&lt;numberOfColumn; column++){
        		TextView textView = new TextView(this);
        		textView.setText("");
        		if(row == 0 &amp;&amp; column == 1) textView.setText("top");
        		if(row == 1 &amp;&amp; column == 0) textView.setText("left");
        		if(row == 1 &amp;&amp; column == 1) textView.setText("center");
        		if(row == 1 &amp;&amp; column == 2) textView.setText("right");
        		if(row == 2 &amp;&amp; column == 1) textView.setText("button");
        		tableRow.addView(textView);
        	}
        	mytable.addView(tableRow,new LayoutParams((cellDimension+2*cellPadding)*numberOfColumn,cellDimension +2*cellPadding));
        }
    }
}

&lt;p&gt;一个实现&amp;#xff0c;供参考&amp;#xff1a;&lt;/p&gt;
&lt;pre&gt;&lt;code class="language-c"&gt;&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;(&lt;span class="hljs-type"&gt;void&lt;/span&gt;)&lt;/span&gt;&lt;/span&gt;{


    &lt;span class="hljs-type"&gt;int&lt;/span&gt; a[&lt;span class="hljs-number"&gt;10&lt;/span&gt;]; &lt;span class="hljs-comment"&gt;//因为数组的长度不超过10&amp;#xff0c;所以定义数组a为11个整数长度就可以了。 &lt;/span&gt;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; n;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Input n:&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;n);

    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Input %d numbers&amp;#xff1a;&amp;#34;&lt;/span&gt;,n);
    &lt;span class="hljs-comment"&gt;//获取输入到数组a &lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;n;i&amp;#43;&amp;#43;){

        &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d&amp;#34;&lt;/span&gt;,&amp;amp;a[i]);
    } 



    &lt;span class="hljs-type"&gt;int&lt;/span&gt; *p&amp;#61;a;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; j;
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; temp&amp;#61;*(p&amp;#43;i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;);  &lt;span class="hljs-comment"&gt;//保存最后一个元素的位置 &lt;/span&gt;
    &lt;span class="hljs-comment"&gt;//利用指针将数组a的元素往后移动一个位置 &lt;/span&gt;
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;i&lt;span class="hljs-number"&gt;-1&lt;/span&gt;;j&amp;gt;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j--){  &lt;span class="hljs-comment"&gt;//从后往前逐个复制其值&amp;#xff0c;达到数组元素往后移动一个位置的目的 &lt;/span&gt;
        *(p&amp;#43;j)&amp;#61;*(p&amp;#43;j&lt;span class="hljs-number"&gt;-1&lt;/span&gt;);
    }
    *(p&amp;#43;j)&amp;#61;temp;  &lt;span class="hljs-comment"&gt;//把最后一个元素移动到第一个元素 &lt;/span&gt;

    &lt;span class="hljs-type"&gt;int&lt;/span&gt; k;    
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(k&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;k&amp;lt;n;k&amp;#43;&amp;#43;){
        &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%4d&amp;#34;&lt;/span&gt;,a[k]);

    }

    &lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
} 

&lt;/code&gt;&lt;/pre&gt;
