  }
&lt;span class="hljs-comment"&gt;// 计算对角线的值&lt;/span&gt;
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;i&amp;lt;&lt;span class="hljs-number"&gt;5&lt;/span&gt;;i&amp;#43;&amp;#43;)
&lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(j&amp;#61;&lt;span class="hljs-number"&gt;0&lt;/span&gt;;j&amp;lt;&lt;span class="hljs-number"&gt;5&lt;/span&gt;;j&amp;#43;&amp;#43;)
{
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i&amp;#61;&amp;#61;j)
sum1 &amp;#61; sum1 &amp;#43; a[i][j];
&lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i&amp;#43;j&amp;#61;&amp;#61;&lt;span class="hljs-number"&gt;4&lt;/span&gt;)
sum2 &amp;#61; sum2 &amp;#43; a[i][j];
}
&lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d %d&amp;#34;&lt;/span&gt;,sum1,sum2);
&lt;span class="hljs-keyword"&gt;return&lt;/span&gt; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
