&lt;p&gt;&lt;/p&gt;&lt;p class="md_img_container"&gt;&lt;img width="600px" align="left" alt="img" src="https://img-mid.csdnimg.cn/release/static/image/mid/ask/868898527436149.png" /&gt;&lt;/p&gt;&lt;p&gt;&lt;/p&gt;

&lt;pre&gt;&lt;code class="language-c"&gt;
&lt;span class="hljs-meta"&gt;#&lt;span class="hljs-keyword"&gt;include&lt;/span&gt; &lt;span class="hljs-string"&gt;&amp;lt;stdio.h&amp;gt;&lt;/span&gt;&lt;/span&gt;

&lt;span class="hljs-function"&gt;&lt;span class="hljs-type"&gt;int&lt;/span&gt; &lt;span class="hljs-title"&gt;main&lt;/span&gt;&lt;span class="hljs-params"&gt;()&lt;/span&gt;

&lt;/span&gt;{
    &lt;span class="hljs-type"&gt;int&lt;/span&gt; i,m,n,sum &amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;;
    &lt;span class="hljs-built_in"&gt;printf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;Enter m, n:\n&amp;#34;&lt;/span&gt;);
    &lt;span class="hljs-built_in"&gt;scanf&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;%d,%d&amp;#34;&lt;/span&gt;,&amp;amp;m,&amp;amp;n);
    &lt;span class="hljs-keyword"&gt;for&lt;/span&gt;(i &amp;#61; m;i &amp;lt;&amp;#61; n;i&amp;#43;&amp;#43;){
        &lt;span class="hljs-keyword"&gt;if&lt;/span&gt;(i % &lt;span class="hljs-number"&gt;3&lt;/span&gt; !&amp;#61; &lt;span class="hljs-number"&gt;0&lt;/span&gt;){
