&lt;pre&gt;&lt;code class="language-c"&gt;#include &amp;lt;stdio.h&amp;gt;

int mai&lt;span class="hljs-meta"&gt;n&lt;/span&gt;()
{
    char c;
    &lt;span class="hljs-keyword"&gt;FILE&lt;/span&gt; &lt;span class="hljs-comment"&gt;*fp1, *fp2;&lt;/span&gt;

    fp1 &amp;#61; &lt;span class="hljs-meta"&gt;fopen&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;file1.dat&amp;#34;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#34;r&amp;#34;&lt;/span&gt;);
    fp2 &amp;#61; &lt;span class="hljs-meta"&gt;fopen&lt;/span&gt;(&lt;span class="hljs-string"&gt;&amp;#34;file2.dat&amp;#34;&lt;/span&gt;, &lt;span class="hljs-string"&gt;&amp;#34;w&amp;#43;&amp;#34;&lt;/span&gt;);
